  }

  &__list-item {
    margin-left: 20px;
  }

  &__list-link {
    display: inline-block;
    text-transform: uppercase;
    font-weight: $primary-fw;
    font-size: $secondary-fs;
    color: #000;
    line-height: 60px;
    position: relative;

    &:hover {
      color: $secondary-color;
    }
  }

  .is-active {
    color: $secondary-color;
  }

  .is-active::after {
    content: "";
    display: block;
    height: 4px;
    width: $max-width;
    background-color: $secondary-color;
    position: absolute;
    bottom: 0;
    border-radius: 8px;
  }

  &__list-item:first-child {
    margin-left: 0;
  }
}

