.card {
  display: flex;
  flex-direction: column;
  align-items: center;
  box-sizing: border-box;
  width: 198px;
  padding: 32px 16px 16px;

  &__image {
    height: 134px;
    width: 160px;
    margin-bottom: 40px;
    background-size: contain;
  }

  &__item {
    &-name {
      color: $main-color;
      font-weight: 500;
      font-size: 12px;
      line-height: 18px;
    }
    &-product-code{
      font-size: 10px;
      align-self: flex-start;
      color: $secondary-color;
      line-height: 14px;
      margin-top: 4px;
    }
  }

  &__price {
    display: flex;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    margin-bottom: 16px;
    &-text {
      font-size: 12px;
      color: $secondary-color;
      line-height: 18px;
    }
    &-amount {
      color: $main-color;
      font-size: 16px;
      font-weight: 700;
      line-height: 18px;
    }
  }

  &__button {
    box-sizing: border-box;
    height: 40px;
    text-transform: uppercase;
    border-radius: 5px;
    width: 166px;
    background-color: $blue;
    color: white;
    font-weight: 700;
    font-size: 14px;
    line-height: 16px;
    border: 1px solid $blue;
    display: flex;
    align-items: center;
    justify-content: center;
    text-decoration: none;
    &:hover {
      background-color: white;
      color: $blue;
      border: 1px solid $blue;
      cursor: pointer;
    }
  }
}
