commits.content:
.nav {
  &__list {
  display: flex;
  list-style: none;
  text-transform: uppercase;
  padding: 0;
  margin: 0;

  /* margin: 0 50px; */

  /* musi być opcja druga, poniewaz dodaje 12px marginu na gorze i na dole */
  }
  &__item {
  position: relative;
  display: flex;
  align-items: center;
  margin: 0 10px;
  height: 60px;
    &:nth-child(1) {
    margin-left: 0;
    }
    &:nth-last-child(1) {
    margin-right: 0;
    }
  }
  &__link {
    display: flex;
    align-items: center;
    color: $main-color;
    text-decoration: none;
    text-align: center;
    height: 60px;
    &:hover {
      color: $blue;
    }
  }
}
