Index: 
===================================================================
--- 
+++ 
@@ -0,0 +1,85 @@
+.product-card {
+  max-width: 200px;
+  border: 1px solid #F3F3F3;
+  border-radius: 5px;
+  background-color: #fff;
+  padding: 32px 16px 16px;
+  transition: transform 300ms ease;
+
+
+
+  &__image {
+    display: flex;
+    width: 160px;
+    height: 134px;
+    margin: 0 auto;
+  }
+
+  &__title {
+    padding-top: 40px;
+    font-size: 12px;
+    line-height: 18px;
+    font-weight: 500;
+    color: #060B35;
+  }
+
+  &__description {
+    padding-top: 4px;
+    font-size: 10px;
+    line-height: 14px;
+    color: #616070;
+  }
+
+  &__rating {
+    display: flex;
+    justify-content: space-between;
+    padding-top: 16px;
+  }
+
+  &__rating-reviews {
+    font-size: 10px;
+    color: #060B35;
+    display: flex;
+    align-items: center;
+  }
+
+  &__price {
+    display: flex;
+    justify-content: space-between;
+    padding-top: 24px;
+    line-height: 18px;
+    padding-bottom: 16px;
+
+    &-name {
+        font-size: 12px;
+        color: #616070;
+      }
+
+    &-value {
+      font-size: 16px;
+      font-weight: bold;
+    }
+  }
+
+
+
+  &__button {
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    height: 39px;
+    background-color: #00ACDC;
+    border-radius: 5px;
+    font-weight: bold;
+    font-size: 14px;
+    color: #fff;
+    text-decoration: none;
+    text-transform: uppercase;
+  }
+
+  &__button:hover {
+    background-color: #fff;
+    border: 1px solid #00ACDC;
+    color: #00ACDC;
+  }
+}

